mapper.select:
SELECT 

FROM ad_infomation a

WHERE a.id = #{id}

SELECT

FROM ad_infomation a

WHERE a.article_id = #{id}

SELECT
FROM ad_infomation a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.release_time LIKE #{releaseTime}||'%' #{releaseTime}+'%' concat(#{releaseTime},'%')
AND a.sold_out_time LIKE #{soldOutTime}||'%' #{soldOutTime}+'%' concat(#{soldOutTime},'%')
AND a.category_id = #{category.id} OR (a.article_id is null and a.del_flag = #{DEL_FLAG_NORMAL})
AND a.promulgator = #{promulgator}
AND a.promulgator = #{promulgator}
ORDER BY a.sort ASC

SELECT

FROM ad_infomation a


	a.del_flag = #{DEL_FLAG_NORMAL} AND a.article_id is not null

		AND a.category_id = #{category.id}


		AND a.parent_id = #{id}


		AND a.release_time <= #{nowDate}
		AND a.sold_out_time >= #{nowDate}


ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM ad_infomation a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE ad_infomation SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE ad_infomation SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'#{id}',%'
