mapper.select:
SELECT 

FROM ad_requirement a

WHERE a.id = #{id}

SELECT
FROM ad_requirement a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' concat('%',#{title},'%')
AND a.email LIKE '%'||#{email}||'%' '%'+#{email}+'%' concat('%',#{email},'%')
AND a.wechat_id LIKE '%'||#{wechatId}||'%' '%'+#{wechatId}+'%' concat('%',#{wechatId},'%')
AND a.create_date = #{createDate}
AND a.create_by = #{createBy.id}
AND a.status = #{status}
ORDER BY a.status asc,${page.orderBy}
ORDER BY a.status asc,a.update_date DESC

SELECT 

FROM ad_requirement a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE ad_requirement SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE ad_requirement SET
	status = #{status}
WHERE id = #{id}
