mapper.select:
SELECT

FROM cms_article a

WHERE a.id = #{id}

SELECT
FROM cms_article a
a.del_flag = #{delFlag}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
AND a.keywords LIKE '%'||#{keywords}||'%' '%'+#{keywords}+'%' CONCAT('%', #{keywords}, '%')
AND a.posid LIKE '%'||#{posid}||'%' '%'+#{posid}+'%' CONCAT('%', #{posid}, '%')
AND (a.category_id = #{category.id}
or c.parent_ids like '%'||#{category.id}||'%' '%,'+#{category.id}+',%' CONCAT('%,', #{category.id}, ',%')
)
AND a.image = #{image}
AND a.id = #{id}
AND (a.company like '%'||#{company}||'%' '%'+#{company}+'%' CONCAT('%', #{company}, '%') or a.posts like '%'||#{company}||'%' '%'+#{company}+'%' CONCAT('%', #{company}, '%') )
AND a.create_by = #{createBy.id}
AND a.is_recommend = #{isRecommend}
AND a.is_top = #{isTop}
AND a.is_read = #{isRead}
ORDER BY a.create_date DESC,a.hits DESC
ORDER BY a.hits DESC,a.is_recommend DESC,a.create_date DESC
ORDER BY ${page.orderBy}
ORDER BY a.weight DESC,a.is_recommend DESC,a.create_date DESC
ORDER BY a.weight DESC, a.is_recommend DESC,a.update_date DESC

SELECT

FROM cms_article a


    a.del_flag = #{DEL_FLAG_NORMAL}



        ORDER BY ${page.orderBy}


        ORDER BY a.weight DESC, a.update_date DESC

SELECT

FROM cms_article a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.is_recommend = #{isRecommend}


        AND a.comment_num = 0


        AND a.category_id = #{category.id}




        ORDER BY a.create_date DESC,${page.orderBy}


        ORDER BY a.create_date DESC

SELECT

FROM cms_article a


    a.del_flag = #{delFlag}
    AND a.category_id in (select categoryId from cms_is_article)

        AND a.title like
        '%'||#{title}||'%'
        '%'+#{title}+'%'
        CONCAT('%', #{title}, '%')


        AND a.image = #{image}


        AND a.id = #{id}


        AND a.create_by = #{createBy.id}


        AND (a.company like
        '%'||#{company}||'%'
        '%'+#{company}+'%'
        CONCAT('%', #{company}, '%')
        or a.posts like
        '%'||#{company}||'%'
        '%'+#{company}+'%'
        CONCAT('%', #{company}, '%')
        )


        AND a.is_recommend = #{isRecommend}


        AND a.is_top = #{isTop}


        AND a.is_read = #{isRead}




        ORDER BY a.hits DESC,a.weight DESC,a.create_date DESC


        ORDER BY ${page.orderBy}


        ORDER BY a.weight DESC, a.create_date DESC


        ORDER BY a.weight DESC, a.create_date DESC

SELECT

FROM cms_article a


    a.del_flag = #{DEL_FLAG_NORMAL}

        AND a.pay = #{pay}


        AND a.area = #{area}


        AND a.education = #{education}


        AND a.experience = #{experience}


        AND c.parent_ids like
        '%'||#{category.id}||'%'
        '%,'+#{category.id}+',%'
        CONCAT('%,', #{category.id}, ',%')





        ORDER BY ${page.orderBy}


        ORDER BY a.weight DESC, a.update_date DESC

select count(id)
from cms_article a

    del_flag != '1'

        AND a.category_id = #{categoryId}


        AND a.create_by = #{createBy}

mapper.update:
UPDATE cms_article SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

UPDATE cms_article SET
	del_flag = #{delFlag},
	promulgator = #{promulgatorId}
WHERE id = #{id}

UPDATE cms_article SET
	like_num = #{likeNum}
WHERE id = #{id}

UPDATE cms_article SET
	is_recommend = #{isRecommend}
WHERE id = #{id}

UPDATE cms_article SET
	is_top = #{isTop}
WHERE id = #{id}

UPDATE cms_article SET
	collect_num = #{collectNum}
WHERE id = #{id}

UPDATE cms_article SET
	comment_num = #{commentNum}
WHERE id = #{id}

UPDATE cms_article SET
	hits = #{hits}
WHERE id = #{id}

mapper.select:
SELECT
from cms_article a
a.del_flag = '0'
AND a.category_id in #{item}
AND a.page_num = #{pageNum}
ORDER BY a.weight_date desc

mapper.update:
update cms_article set
	 hits = hits+1
 WHERE id = #{id}

mapper.select:
select max(c.id) AS "id", max(c.name) AS "name", max(cp.id) AS "parent.id", max(cp.name) AS "parent.name", count(*) AS "cnt", sum(a.hits) AS "hits", max(a.update_date) as "updateDate", max(o.id) AS "office.id", max(o.name) AS "office.name" FROM cms_article a RIGHT JOIN cms_category c ON c.id = a.category_id JOIN cms_category cp ON c.parent_id = cp.id JOIN sys_office o ON o.id = c.office_id
a.del_flag = #{DEL_FLAG_NORMAL} AND c.site_id = #{site.id}
AND (c.office_id = #{office.id} OR o.parent_ids like '%'||#{office.id}||'%' '%'+#{office.id}+'%' CONCAT('%', #{office.id}, '%'))
AND a.update_date >= #{beginDate}
AND a.update_date <= #{endDate}
AND (c.id = #{id} OR c.parent_ids LIKE '%'||#{id}||'%' '%'+#{id}+'%' CONCAT('%', #{id}, '%'))
group by cp.sort, cp.id, c.sort, c.id order by cp.sort, cp.id, c.sort, c.id
