SELECT 

FROM cms_category a


	a.del_flag = #{delFlag}

		AND a.site_id  = #{site.id}


		AND a.parent_id  = #{parent.id}


		AND a.is_show_home  = #{isShowHome}

	${sqlMap.dsf}

	ORDER BY a.site_id,a.sort ASC

SELECT 

FROM cms_category a


	a.del_flag = #{DEL_FLAG_NORMAL}

		AND a.site_id  = #{site.id}


		AND a.parent_id  = #{parent.id}


		AND a.in_menu  = #{inMenu}

