mapper.select:
SELECT 

FROM cms_comment a

WHERE a.id = #{id}

SELECT

FROM cms_comment a

WHERE a.id = #{parentContentId}

SELECT DISTINCT a.content_id FROM cms_comment a
1=1
AND a.create_by = #{createBy.id}

SELECT
FROM cms_comment a
a.del_flag = #{delFlag}
AND a.title LIKE '%'||#{title}||'%' '%'+#{title}+'%' CONCAT('%', #{title}, '%')
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

SELECT

FROM cms_comment a


	a.del_flag = #{delFlag}

		AND a.title LIKE
			'%'||#{title}||'%'
			'%'+#{title}+'%'
			CONCAT('%', #{title}, '%')


		AND a.parent_content_id is null
		AND a.comment_num = '0'


		AND a.content_id = #{contentId}


		AND a.category_id in (select categoryId from cms_is_article)


		AND a.category_id = #{category.id}


		AND a.is_recommend = #{isRecommend}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC ,a.is_top DESC

SELECT 

FROM cms_comment a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

mapper.update:
UPDATE cms_comment SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
